function GetLangRightID($vRightID,$vlang)
{
	$vArrLang=Array('VN'=>'VN','EN'=>'EN');
	if($vlang!='VN' && $vlang!='EN')
	{
		$vTT=0;
		foreach($vArrLang as $tLang)
		{
			if($vTT>0)
				$tLangStr=$tLangStr."<br/>".GetLangRightIDChild($vRightID,$tLang);
			else
				$tLangStr=GetLangRightIDChild($vRightID,$tLang);
			$vTT++;
		}
		return $tLangStr;
	}
	else
	{
		return GetLangRightIDChild($vRightID,$vlang);
	}
}
